import axios from '../common/js/axios'

export function topics(tab = '', page = 1, limit = 40) {
  const data = {
    tab,
    page,
    limit
  }
  return axios.get('https://cnodejs.org/api/v1/topics', params = data).then(res => {
    return Promise.resolve(res.data)
  })
}
