import axios from '../common/js/axios'

export function topics(page,tab,limit) {
  const data = {
    tab,
    page,
    limit
  }
  return axios.get('https://cnodejs.org/api/v1/topics').then(res => {
    return Promise.resolve(res.data)
  })
}
