import React, { useState, useEffect } from 'react'
import Link from 'next/link'
import Router from 'next/router'

import styles from './content.sass'

const Content = ({ topic, changeTopic }) => {
  const nav = [
    {
      key: 'ask',
      name: '问答'
    },
    {
      key: 'share',
      name: '分享'
    },
    {
      key: 'job',
      name: '招聘'
    },
    {
      key: 'good',
      name: '精华'
    }
  ]

  const [routeName, setRouteName] = useState('')
  useEffect(() => {
    setRouteName(Router.router.query.name)
  }, [])

  const handleData = key => {
    changeTopic(key)
  }

  return (
    <div className={styles.content}>
      <div className={styles.nav}>
        <ul className={styles.navList}>
          <li>
            <Link href={{ pathname: '/' }}>
              <a
                onClick={handleData}
                className={routeName ? '' : styles.highLight}
              >
                全部
              </a>
            </Link>
          </li>
          {nav.map((item, index) => (
            <li key={index + item.key}>
              <Link
                href={{ pathname: '/', query: { name: item.key } }}
                as={`/${item.key}`}
              >
                <a
                  onClick={() => {
                    handleData(item.key)
                  }}
                  className={routeName === item.key ? styles.highLight : ''}
                >
                  {item.name}
                </a>
              </Link>
            </li>
          ))}
        </ul>
      </div>
      <div className={styles.list}>
        <ul className={styles.listContainer}>
          {topic.map(item => (
            <li key={item.id} className={styles.item}>
              <div className={styles.avatar}>
                <img
                  src={item.author.avatar_url}
                  alt={item.author.loginname}
                  width='30'
                  height='30'
                />
              </div>
              <div className={styles.reading}>190/56870</div>
              <div className={styles.title}>
                {item.good || item.top ? (
                  <span className={styles.top}>
                    {item.top ? '置顶' : '精华'}
                  </span>
                ) : (
                  <span className={styles.share}>
                    {item.tab === 'ask' ? '问答' : '分享'}
                  </span>
                )}
                {item.title}
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  )
}

export default Content
